import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { TrailerDetailService } from './trailer-detail.service';
import { DomSanitizer } from '@angular/platform-browser';
import { NgxSpinnerService } from 'ngx-spinner';
import { ActivatedRoute, Router } from '@angular/router';
import { catchError, last, of } from 'rxjs';
import Swal from 'sweetalert2';
import { HttpErrorResponse } from '@angular/common/http';
import { MasterModule } from '../../master.module';
import { error } from 'console';
import { NavBarComponent } from "../../../theme/layouts/admin-layout/nav-bar/nav-bar.component";
import moment from 'moment';

@Component({
  selector: 'app-trailer-detail',
  standalone: true,
  imports: [MasterModule],
  templateUrl: './trailer-detail.component.html',
  styleUrl: './trailer-detail.component.scss'
})
export class TrailerDetailComponent implements OnInit{
  trailerForm: FormGroup;
  id: string;
  breadCrumbItems: Array<{}>;
  isAdd: boolean = true;
  containerSizeList: any[] = [20, 40, 45];
  containerTypeList: any[] = ["DV","FR","GP", "HC", "HQ","HG","OS","OT","PF","RF","RH","TK", "IC", "FL", "BC", "HT", "VC", "PL"];
  transporterNames:any[]=[];
  driverLicenseNoList:any[];
  formatfilter:string='dd-MMM-yyyy';
  today : Date = new Date();
  submitClicked: boolean = false;

  constructor(
    private _sanitizer: DomSanitizer,
    private service: TrailerDetailService,
    private spinner: NgxSpinnerService,
    private route: ActivatedRoute,
    private router: Router,
  ) {}

  ngOnInit(): void {
    this.breadCrumbItems = [{ label: 'Trailer List', routerLink: '/trailer-detail', active: false },
    { label: 'Add New Trailer', active: true }];
    this.id = this.route.snapshot.queryParams['id'];
    this.trailerForm = new FormGroup({
      vehicleRegNo: new FormControl({value:this.id,disabled:!!this.id },Validators.required),
      vehicleBackRegNo: new FormControl(''),
      containerType: new FormControl(''),
      containerSize:new FormControl(''),
      transporterID:new FormControl('',Validators.required),
      trailerWeight:new FormControl(''),
      driverLicenseNo:new FormControl(''),
      remarks:new FormControl(''),
      active: new FormControl(false),
      // lastPassedDate:new FormControl(null),
      isBlack:new FormControl(''),
      
    });
    if (this.id) {
      this.getTrailerById();
      this.isAdd=true;
    }

    this.service.getDriverList('true', 'false').subscribe({
      next: (LicenseNoList:any[]) => { 
        console.log("Driver License and Names Loaded:", LicenseNoList);
        this.driverLicenseNoList = LicenseNoList; 
      },
      error: (error: any) => {
        console.error('Error Loading Transporter Names:', error); 
      }
    });
    
    this.service.getTransporterList('true','false').subscribe({
      next:(names)=>{
        console.log("Transporter Names Loaded:",names);
        this.transporterNames=names;
      },
      error:(error)=>{
        console.log('Error Loading Transporter Names',error)
      }
    });
  }

  getTrailerById() {
    this.spinner.show();
    this.service.getTrailerId(this.id)
   .pipe(catchError((err) => of(this.showError(err))))
     .subscribe((result) => {
      this.trailerForm.controls['vehicleRegNo'].setValue(result.vehicleRegNo);
      this.trailerForm.controls['vehicleBackRegNo'].setValue(result.vehicleBackRegNo);
      this.trailerForm.controls['containerType'].setValue(result.containerType);
      this.trailerForm.controls['containerSize'].setValue(result.containerSize);
      this.trailerForm.controls['transporterID'].setValue(result.transporterID);
      this.trailerForm.controls['trailerWeight'].setValue(result.trailerWeight);
      this.trailerForm.controls['driverLicenseNo'].setValue(result.driverLicenseNo);
      this.trailerForm.controls['remarks'].setValue(result.remarks);
      this.trailerForm.controls['active'].setValue(result.active);
      this.trailerForm.controls['isBlack'].setValue(result.isBlack);
      // this.trailerForm.controls['lastPassedDate'].setValue(result.lastPassedDate);

      this.isAdd=false;
      this.breadCrumbItems = [{ label: 'Trailer',rounterLink:'/trailer-detail',active:false }, { label: 'Edit Trailer', active: true }];
      this.spinner.hide();
    });
  }

  saveTrailer() {
    const formData = this.trailerForm.value;
    if (this.isAdd) {
      formData.createdUser = localStorage.getItem('currentUser');
      formData.vehicleRegNo=formData.vehicleRegNo.toUpperCase();
      formData.vehicleBackRegNo=formData.vehicleBackRegNo.toUpperCase();
      formData.isBlack=formData.isBlack;
      this.addNewTrailer(formData);
    } else {
      formData.updatedUser = localStorage.getItem('currentUser');
      this.editTrailer(formData);
    }
  }
  navigateToTrailer() {
    this.router.navigate(['master/trailer']);
  }

  addNewTrailer(data: any) {
    this.spinner.show();
    const formData = {
      VehicleRegNo: data.vehicleRegNo,
      VehicleBackRegNo: data.vehicleBackRegNo,
      ContainerType: data.containerType,
      ContainerSize: data.containerSize ?? '',
      TransporterID: data.transporterID,
      TrailerWeight: data.trailerWeight ?? '',
      DriverLicenseNo: data.driverLicenseNo,
      Remarks: data.remarks,
      Active: data.active,
      IsBlack: data.isBlack,
      // LastPassedDate: moment(data.lastPassedDate).format('YYYY/MM/DD'),
    }
    this.service.createTrailer(formData)
      .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.spinner.hide();
        if (result.status) {
          this.showSuccess('Trailer Added Successfully!');
          this.router.navigate(["master/trailer"]);
        } else {
          Swal.fire('Trailer', result.messageContent, 'error');
        }
      });
  }

  editTrailer(data: any) {
    this.spinner.show();
    const formData = {
      VehicleRegNo: this.id,
      VehicleBackRegNo: data.vehicleBackRegNo ?? "",
      ContainerType: data.containerType,
      ContainerSize: data.containerSize ?? "",
      TransporterID: data.transporterID,
      TrailerWeight: data.trailerWeight ?? "",
      DriverLicenseNo: data.driverLicenseNo,
      Remarks: data.remarks ?? "",
      Active: data.active,
      IsBlack: data.isBlack,
      // LastPassedDate: moment(data.lastPassedDate).format('YYYY/MM/DD'),
    };
    this.service.updateTrailer(formData)
      .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.spinner.hide();
        if (result.status) {
          this.showSuccess('Trailer Updated Successfully!');
          this.router.navigate(["master/trailer"]);
        } else {
          Swal.fire('Trailer', result.messageContent, 'error');
        }
      });
}

  showSuccess(msg: string) {
    Swal.fire('Trailer', msg, 'success');
  }

  showError(error: HttpErrorResponse) {
    Swal.fire('Trailer', error.statusText, 'error');
  }

  validateControl(controlName: string) {
    const control = this.trailerForm.get(controlName);
    return (control.invalid && (control.dirty || control.touched)) || (control.invalid && this.submitClicked);
  }

}
<section class="content mt-0">
    <div class="container-fluid">
        <div class="row">
            <div class="col-md-12 ">
              <ol class="breadcrumb">
                <li class="breadcrumb-item"><a routerLink="/dashboard" routerLinkActive="active"><i class="nav-icon fas fa-tachometer-alt"></i>&nbsp;Dashboard</a></li>
                <li class="breadcrumb-item">Trailer</li>
                <li class="breadcrumb-item active text-primary">Trailer Info</li>
              </ol>
            </div>
        </div>
        <div class="row">
            <div class="card card-primary card-outline">
                <div class="card-body">
                    <form [formGroup]="trailerForm">
                      <div class="row">
                        <div class="col-md-6 col-lg-6 text-left">
                          <h5 class="card-title">Trailer Info</h5>
                        </div>

                        <div class="col-md-6 col-lg-6 text-end">
                          <button type="submit" class="btn btn-sm btn-primary mr-2 " [disabled]="!trailerForm.valid"  *ngIf="isAdd" (click)="saveTrailer()"><i class="fa fa-save"></i>  &nbsp;Save</button>
                          <button type="submit" class="btn btn-sm btn-primary mr-2 " [disabled]="!trailerForm.valid" *ngIf="!isAdd"  (click)="saveTrailer()"><i class="fa fa-save"></i>  &nbsp;Update</button>
                          <a routerLink="master/trailer" class="btn btn-sm btn-secondary m-1" href="master/trailer">
                            <i class="fa fa-arrow-circle-left"></i> &nbsp; Back
                          </a>
                        </div>
                      </div><br>
                      <div class="row my-0">
                        <div class="form-group col-md-4">
                          <label class="font-weight-normal requiredfield" *ngIf="isAdd">Vehicle Reg No.</label>
                          <label class="font-weight-normal requiredfield" *ngIf="!isAdd">Vehicle Reg No.</label>
                            <ejs-textbox
                                formControlName="vehicleRegNo" class="text-uppercase"
                                tabindex="0" maxlength="50" />
                                <div *ngIf="trailerForm.controls['vehicleRegNo'].invalid && (trailerForm.controls['vehicleRegNo'].touched || trailerForm.controls['vehicleRegNo'].dirty)">
                                  <small class="text-danger">This field is required.</small>
                              </div>
                        </div>
                        <div class="form-group col-md-4">
                          <label class="font-weight-normal">Vehicle Back RegNo.</label>
                            <ejs-textbox
                                formControlName="vehicleBackRegNo"
                                class="text-uppercase"
                                autocomplete="off"
                                tabindex="1" maxlength="100"/>
                        </div>
                        <div class="form-group col-md-4">
                          <label class="font-weight-normal">Container Type</label>
                            <ejs-dropdownlist
                                formControlName="containerType"
                                [dataSource]="containerTypeList"
                                placeholder="Choose Container Type"
                                autocomplete="off"
                                tabindex="2"
                                maxlength="100" />
                        </div>
                      </div>

                      <div class="row mt-2">
                        <div class="form-group col-md-4">
                          <label class="font-weight-normal">Container Size</label>
                            <ejs-dropdownlist
                                formControlName="containerSize"
                                [dataSource]="containerSizeList"
                                autocomplete="off"
                                placeholder="Choose Container Size"
                                tabindex="3"
                                maxlength="100"/>
                        </div>
                        <div class="form-group col-md-4">
                          <label class="font-weight-normal requiredfield">Transporter</label>
                          <ejs-dropdownlist
                            formControlName="transporterID"
                            [dataSource]="transporterNames"
                            [fields]="{ text: 'transporterName', value: 'transporterID' }"
                            autocomplete="off"
                            placeholder="Choose Transpoter"
                            tabindex="4"
                            maxlength="100">
                          </ejs-dropdownlist>
                          <div *ngIf="trailerForm.controls['transporterID'].invalid && (trailerForm.controls['transporterID'].touched || trailerForm.controls['transporterID'].dirty)">
                            <small class="text-danger">This field is required.</small>
                        </div>
                        </div>
                          <div class="form-group col-md-4">
                            <label class="font-weight-normal">Trailer Weight</label>
                              <ejs-numerictextbox
                                  formControlName="trailerWeight"
                                  placeholder="Trailer Weight"
                                  [min]="0"
                                  [max]="100000"
                                  [decimals]="2"
                                  autocomplete="off"
                                  tabindex="5"
                                  maxlength="100"/>
                          </div>
                      </div>
                      
                      <div class="row mt-2">
                          <div class="form-group col-md-4">
                            <label class="font-weight-normal">Driver</label>
                              <ejs-dropdownlist
                                  formControlName="driverLicenseNo"
                                  [dataSource]="driverLicenseNoList"
                                  [fields]="{text:'driverName',value:'licenseNo'}"
                                  placeholder="Choose Driver"
                                  autocomplete="off"
                                  tabindex="7"
                                  maxlength="100"/>
                          </div>
                          <div class="form-group col-md-4">
                            <label >Remarks</label>
                            <textarea formControlName="remarks" class="form-control form-control-sm"
                            autocomplete="off" placeholder="Remarks "tabindex="9"></textarea>
                          </div>
                          <div class="form-group col-md-4"><br>
                            <ejs-checkbox formControlName="active" tabindex="8" label="IsActive" [checked]="true"></ejs-checkbox>
                          </div>
                      </div>
                      
                    </form>
                </div>
            </div>
        </div>
    </div>
</section>
