export class InCheckDocComponent {
  constructor(service, spinner, route, router) {
    this.service = service;
    this.spinner = spinner;
    this.route = route;
    this.router = router;
    this.pageSettings = {
      pageSize: 10
    };
    this.editSettings = {
      allowEditing: false,
      allowAdding: false,
      allowDeleting: true,
      mode: 'Dialog'
    };
    this.toolbar = [{
      text: "Checked",
      tooltipText: "Checked",
      prefixIcon: "e-icons e-check",
      id: "check"
    }, 'Search'];
    this.lines = 'Both';
    this.submitClicked = false;
    this.interval = 1;
    this.format = "dd/MM/yyyy h:mm:ss a";
    this.cargoTypeList = ['Laden', 'MT'];
    this.typeList = ['FCL', 'LCL'];
    this.yardList = [];
    this.gateList = [];
    this.wbList = [];
    this.truckList = [];
    this.trailerList = [];
    this.driverList = [];
    this.transporterList = [];
    this.areaList = [];
    this.pcCodeList = [];
    this.docList = [];
    this.cardList = [];
    this.truckTypeList = ['RGL', 'Customer', 'Supplier'];
    this.wbOptionList = ['None', 'Single', 'Both'];
    this.billOptionList = ['None', 'Credit', 'Cash'];
    this.endDate = new Date();
    this.isInWb = false;
    this.isOutWb = false;
    this.today = new Date();
    this.placeholder = 'Select One';
    this.filterPlaceholder = 'Search';
    this.searchTruckTerms = new Subject();
    this.searchDriverTerms = new Subject();
    this.onFiltering = e => {};
  }
  ngOnInit() {
    this.id = this.route.snapshot.queryParams['id'];
    this.detailForm = new FormGroup({
      inRegNo: new FormControl(''),
      inCheckDateTime: new FormControl(this.today, Validators.required),
      inGateID: new FormControl('', Validators.required),
      inYardID: new FormControl('', Validators.required),
      inPCCode: new FormControl('', Validators.required),
      truckVehicleRegNo: new FormControl('', Validators.required),
      driverLicenseNo: new FormControl('', Validators.required),
      areaID: new FormControl('', Validators.required),
      driverName: new FormControl(''),
      transporterID: new FormControl(''),
      transporterName: new FormControl(''),
      truckType: new FormControl('', Validators.required),
      trailerVehicleRegNo: new FormControl(''),
      inType: new FormControl(''),
      inCargoType: new FormControl(''),
      inCargoInfo: new FormControl(''),
      cardNo: new FormControl(''),
      remark: new FormControl(''),
      customer: new FormControl(''),
      status: new FormControl(''),
      wbOption: new FormControl('')
    });
    this.cardForm = new FormGroup({
      inRegNo: new FormControl(''),
      cardNo: new FormControl('', Validators.required),
      inWeightBridgeID: new FormControl(''),
      outWeightBridgeID: new FormControl(''),
      inWBBillOption: new FormControl(''),
      outWBBillOption: new FormControl('')
    });
    this.getCategoryList();
    this.getTrailerList();
    this.getLocationList();
    this.getTransporterList();
    if (this.id) {
      this.getInBoundCheckById();
    }
    this.searchTruckTerms.pipe(debounceTime(300), switchMap(term => this.service.getTruckList(term, this.type))).subscribe(data => {
      if (this.type) {
        this.truckList = data;
      } else {
        this.truckList = [];
      }
    });
    this.searchDriverTerms.pipe(debounceTime(300), switchMap(term => this.service.getDriverList(term))).subscribe(data => {
      this.driverList = data;
    });
  }
  getTrailerList() {
    this.service.getTrailerList().pipe(catchError(err => of(this.showError(err)))).subscribe(result => {
      this.trailerList = result;
      this.spinner.hide();
    });
  }
  onTruckFiltering(e) {
    if (e.text && this.type) {
      this.searchTruckTerms.next(e.text);
    }
  }
  onDriverFiltering(e) {
    if (e.text) {
      this.searchDriverTerms.next(e.text);
    }
  }
  getTransporterList() {
    this.service.getTransporterList().pipe(catchError(err => of(this.showError(err)))).subscribe(result => {
      this.transporterList = result;
      this.spinner.hide();
    });
  }
  getLocationList() {
    this.spinner.show();
    this.service.getYardList('true').pipe(catchError(err => of(this.showError(err)))).subscribe(result => {
      this.yardList = result;
      this.spinner.hide();
    });
  }
  getGateList(yard) {
    this.spinner.show();
    this.service.getGateList(yard).pipe(catchError(err => of(this.showError(err)))).subscribe(result => {
      this.gateList = result;
      this.spinner.hide();
    });
  }
  getDocumentSettingList(id) {
    this.spinner.show();
    this.service.getDocumentSettingList(id).pipe(catchError(err => of(this.showError(err)))).subscribe(result => {
      this.docList = result;
      this.spinner.hide();
    });
  }
  getCategoryList() {
    this.service.getCategoryList().pipe(catchError(err => of(this.showError(err)))).subscribe(result => {
      this.pcCodeList = result;
      this.pcCodeList.unshift({
        pcCode: 'None'
      });
      this.spinner.hide();
    });
  }
  rowDataBound(args) {
    if (args.row) {
      //args.isSelectable = args.data.status === 'Draft';
      if (args.data.checkStatus) {
        args.row.classList.add('bluegreen');
      }
    }
  }
  getInBoundCheckById() {
    this.spinner.show();
    this.service.getInBoundCheckById(this.id).pipe(catchError(err => of(this.showError(err)))).subscribe(result => {
      this.docList = result.documentList;
      this.detailData = result;
      for (let key in this.detailData) {
        if (this.detailData.hasOwnProperty(key) && this.detailData[key] != null && this.detailForm.controls[key]) {
          if (key != 'documentList') {
            this.detailForm.controls[key].setValue(this.detailData[key]);
          }
        }
      }
    });
    this.spinner.hide();
  }
  onFormAssignCard() {
    const formData = this.detailForm.value;
    const cardForm = this.cardForm.value;
    formData.cardNo = cardForm.cardNo;
    formData.inWeightBridgeID = cardForm.inWeightBridgeID;
    formData.outWeightBridgeID = cardForm.outWeightBridgeID;
    formData.outWBBillOption = cardForm.outWBBillOption;
    formData.inWBBillOption = cardForm.inWBBillOption;
    formData.documentList = this.docList;
    formData.inRegNo = 0;
    formData.createdUser = localStorage.getItem('currentUser');
    if (this.transporterList) {
      const transporter = this.transporterList.filter(x => x.transporterID == formData.transporterID);
      if (transporter.length > 0) {
        formData.transporterName = transporter[0].transporterName;
      }
    }
