import { HttpErrorResponse } from '@angular/common/http';
import { Component, ViewChild } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { EditSettingsModel, GridComponent, GridLine, PageSettingsModel, SaveEventArgs } from '@syncfusion/ej2-angular-grids';
import { ClickEventArgs } from '@syncfusion/ej2-angular-navigations';
import { catchError, debounceTime,of, Subject, switchMap } from 'rxjs';
import Swal from 'sweetalert2';
import { InCheckService } from '../in-check/in-check.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ActivatedRoute, Router } from '@angular/router';
import { FilteringEventArgs } from '@syncfusion/ej2-angular-dropdowns';
import { EmitType } from '@syncfusion/ej2/base';
import { TmsOperationModule } from '../../tms-operation.module';
import { DialogComponent } from '@syncfusion/ej2-angular-popups';

@Component({
  selector: 'app-in-check-doc',
  standalone: true,
  imports: [TmsOperationModule],
  templateUrl: './in-check-doc.component.html',
  styleUrl: './in-check-doc.component.scss'
})
export class InCheckDocComponent {
  pageSettings: PageSettingsModel = { pageSize: 10 };
  editSettings: EditSettingsModel = { allowEditing: false, allowAdding: false, allowDeleting: true, mode: 'Dialog' };
  toolbar: any[] = [{ text: "Checked", tooltipText: "Checked", prefixIcon: "e-icons e-check", id: "check" },'Search'];
  lines: GridLine = 'Both';
  submitClicked:boolean=false;
  detailForm:any;
  id:any;
  interval: number=1;
  public format="dd/MM/yyyy h:mm:ss a";
  cardForm:any;
  detailData:any;
  cargoTypeList:any[]=['Laden','MT'];
  typeList:string[]=['FCL','LCL'];
  yardList:any[]=[];
  gateList:any[]=[];
  wbList:any[]=[];
  truckList:any[]=[];
  trailerList:any[]=[];
  driverList:any[]=[];
  transporterList:any[]=[];
  areaList:any[]=[];
  pcCodeList:any[]=[];
  docList:any[]=[];
  cardList:any[]=[];
  truckTypeList:any[]=['RGL','Customer','Supplier'];
  wbOptionList:any[]=['None','Single','Both'];
  billOptionList:any[]=['None','Credit','Cash'];
  endDate : Date = new Date();
  type:string;
  isInWb:boolean=false;
  isOutWb:boolean=false;
  today : Date = new Date();
  public data: Object[];
  public placeholder: string = 'Select One';
  public filterPlaceholder: string = 'Search';
  private searchTruckTerms = new Subject<string>();
  private searchDriverTerms = new Subject<string>();
  @ViewChild('cardModel') cardModel: DialogComponent;
  @ViewChild('Grid') public grid: GridComponent;
  constructor(
    private service: InCheckService,
    private spinner: NgxSpinnerService,
    private route: ActivatedRoute,
    private router: Router,

  ) {}

  ngOnInit(){
    this.id = this.route.snapshot.queryParams['id'];
    this.detailForm = new FormGroup({
    inRegNo: new FormControl(''),
    inCheckDateTime: new FormControl(this.today, Validators.required),
    inGateID: new FormControl('',Validators.required),
    inYardID: new FormControl('',Validators.required),
    inPCCode: new FormControl('',Validators.required),
    truckVehicleRegNo: new FormControl('',Validators.required),
    driverLicenseNo: new FormControl('',Validators.required),
    areaID: new FormControl('',Validators.required),
    driverName: new FormControl(''),
    transporterID: new FormControl(''),
    transporterName: new FormControl(''),
    truckType:new FormControl('',Validators.required),
    trailerVehicleRegNo:new FormControl(''),
    inType:new FormControl(''),
    inCargoType:new FormControl(''),
    inCargoInfo:new FormControl(''),
    cardNo:new FormControl(''),
    remark:new FormControl(''),
    customer:new FormControl(''),
    status:new FormControl(''),
    wbOption:new FormControl(''),
    });

    this.cardForm = new FormGroup({
    inRegNo: new FormControl(''),
    cardNo: new FormControl('', Validators.required),
    inWeightBridgeID: new FormControl(''),
    outWeightBridgeID: new FormControl(''),
    inWBBillOption:new FormControl(''),
    outWBBillOption:new FormControl(''),
    });

    this.getCategoryList();
    this.getTrailerList();
    this.getLocationList();
    this.getTransporterList();
    if(this.id){
      this.getInBoundCheckById();
    }
    this.searchTruckTerms.pipe(
      debounceTime(300),
      switchMap((term: string) => this.service.getTruckList(term,this.type))
    ).subscribe(data => {
      if(this.type){
        this.truckList  = data;
      }
      else{
        this.truckList =[];
      }
    });

    this.searchDriverTerms.pipe(
      debounceTime(300),
      switchMap((term: string) => this.service.getDriverList(term))
    ).subscribe(data => {
      this.driverList  = data;
    });
  }

  public onFiltering: EmitType<FilteringEventArgs> = (e: FilteringEventArgs) => {

  }

  getTrailerList(){
    this.service.getTrailerList()
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.trailerList = result;
        this.spinner.hide();
    });
  }

  onTruckFiltering(e: any) {
    if (e.text && this.type) {
      this.searchTruckTerms.next(e.text);
    }
  }

  onDriverFiltering(e: any) {
    if (e.text) {
      this.searchDriverTerms.next(e.text);
    }
  }

  getTransporterList(){
    this.service.getTransporterList()
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.transporterList = result;
        this.spinner.hide();
    });
 }

  getLocationList() {
    this.spinner.show();
    this.service.getYardList('true')
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.yardList = result;
        this.spinner.hide();
    });
  }

  getGateList(yard:string){
    this.spinner.show();
    this.service.getGateList(yard)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.gateList  = result;
        this.spinner.hide();
    });
  }

  getDocumentSettingList(id:string){
    this.spinner.show();
    this.service.getDocumentSettingList(id)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.docList  = result;
        this.spinner.hide();
    });
  }

  getCategoryList(){
     this.service.getCategoryList()
     .pipe(catchError((err) => of(this.showError(err))))
       .subscribe((result) => {
         this.pcCodeList = result;
         this.pcCodeList.unshift({pcCode:'None'});
         this.spinner.hide();
     });
  }


  rowDataBound(args: any): void {
    if (args.row) {
      //args.isSelectable = args.data.status === 'Draft';
      if (args.data.checkStatus) {
        (args.row as Element).classList.add('bluegreen');
      }
     }
   }

  getInBoundCheckById(){
    this.spinner.show();
    this.service.getInBoundCheckById(this.id)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.docList=result.documentList;
        this.detailData = result;

        for (let key in this.detailData) {
          if ( this.detailData.hasOwnProperty(key) && this.detailData[key] != null &&  this.detailForm.controls[key]) {
            if (key != 'documentList') {
              this.detailForm.controls[key].setValue(this.detailData[key]);
            }
          }
         }
        });
        this.spinner.hide();
  }

  onFormAssignCard(){
    const formData=this.detailForm.value;
    const cardForm = this.cardForm.value;
    formData.cardNo = cardForm.cardNo;
    formData.inWeightBridgeID = cardForm.inWeightBridgeID;
    formData.outWeightBridgeID = cardForm.outWeightBridgeID;
    formData.outWBBillOption = cardForm.outWBBillOption;
    formData.inWBBillOption = cardForm.inWBBillOption;
    formData.documentList = this.docList;
    formData.inRegNo=0;
    formData.createdUser = localStorage.getItem('currentUser');
    if(this.transporterList){
      const transporter = this.transporterList.filter(x=>x.transporterID==formData.transporterID);
      if(transporter.length>0){
        formData.transporterName = transporter[0].transporterName;
      }
    }
    if(this.driverList.length>0){
      const driver = this.driverList.filter(x=>x.licenseNo==formData.driverLicenseNo);
      if(driver.length>0){
        formData.driverName = driver[0].name;
        formData.driverContactNo = driver[0].contactNo;
      }
    }
    if(this.isInWb){
      if(!cardForm.inWeightBridgeID){
        Swal.fire('In Check Document', 'Please add In Weight Bridge.', 'warning');
        return;
      }
    }
    if(this.isOutWb){
      if(!cardForm.outWeightBridgeID){
        Swal.fire('In Check Document', 'Please add Out Weight Bridge.', 'warning');
        return;
      }
    }
      this.createInBoundCheck(formData);
  }

  onFormSubmit(){
   this.spinner.show();
   const formData=this.detailForm.value;
   const doc = this.docList.filter(x=>x.checkStatus!=true);
   if(doc.length>0 && this.docList.length>0){
    Swal.fire('In Check Document', 'Please check document.', 'warning');
   }
   else{
      if(formData.wbOption=='Single'){
        this.isInWb=true;
        this.isOutWb=false;
      }
      else if (formData.wbOption=='Both'){
        this.isInWb=true;
        this.isOutWb=true;
      }
      else{
        this.isInWb=false;
        this.isOutWb=false;
      }
      if(this.isInWb || this.isOutWb){
        this.getWBDataList(formData.inYardID)
      }
      this.getCardList(formData.inYardID);
      this.cardModel.show();
   }
   this.spinner.hide();
  }

  onBackSubmit(){
    this.router.navigate(["/tms-operation/in-check"]);
  }

  createInBoundCheck(formData: any) {
    this.spinner.show();
    this.service
    .createInBoundCheck(formData)
    .pipe(catchError((err) => of(this.showError(err))))
    .subscribe((result) => {
      if (result.status == true) {
        this.showSuccess(result.messageContent);
        this.router.navigate(["/tms-operation/in-check"]);
      } else {
        this.spinner.hide();
        Swal.fire('In Check Document', result.messageContent, 'error');
      }
    });
  }

  onYardChange(code: string) {
    this.getGateList(code);
    this.getAreaList(code);
   }

   onCategoryChange(code: string) {
    this.getDocumentSettingList(code);
   }

   onTruckTypeChange(code: string) {
     this.type = code;
     this.truckList =[];
    }

    onTruckChange(id:string){
     const truck = this.truckList.filter(x=>x.vehicleRegNo==id);
     if(this.driverList){
      const driver = this.driverList.filter(x=>x.licenseNo==truck[0].driverLicenseNo);
      if(driver.length>0){
        this.detailForm.controls['driverLicenseNo'].setValue(truck[0].driverLicenseNo?truck[0].driverLicenseNo:null);
      }
     }
      this.detailForm.controls['transporterID'].setValue(truck[0].transporterID?truck[0].transporterID:null);
    }


  getAreaList(yard:string){
    this.spinner.show();
    this.service.getAreaList(yard)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.areaList  = result;
        this.spinner.hide();
    });
  }

  getCardList(yard:string){
    this.spinner.show();
    this.service.getCardICDList(yard)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.cardList  = result;
        this.spinner.hide();
    });
  }

  getWBDataList(yard:string){
    this.spinner.show();
    this.service.getWBDataList(yard)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.wbList  = result;
        this.wbList.unshift({weightBridgeID:'None'});
        this.spinner.hide();
    });
  }

  showError(error:HttpErrorResponse){
    this.spinner.hide();
    Swal.fire('In Check Document',error.statusText,'error');
  }
  showSuccess(msg: string) {
    this.spinner.hide();
    Swal.fire('In Check Document', msg, 'success');
  }

  validateControl(controlName: string) {
    const control = this.detailForm.get(controlName);
    return (control.invalid && (control.dirty || control.touched)) || (control.invalid && this.submitClicked);
  }

  toolbarClick(args: ClickEventArgs): void {
    if (args.item.id === 'check') {
      let selectedRecords: any[] = this.grid.getSelectedRecords();
      if (selectedRecords.length == 0) {
        Swal.fire('Check Document', "Please select one row!", 'warning');
      }
      else {
        if (args.item.id === 'check')
        {
          for(var i=0;i<selectedRecords.length;i++){
            this.docList = this.docList.map((x) => {
              if (x.docCode === selectedRecords[i].docCode) {
                  return { ...x, docCode:  selectedRecords[i].docCode,docName: selectedRecords[i].docName,checkStatus: true};//update data
              } else {
                  return x; // Keep other objects unchanged
             }
             });
           }
        }
        return;
      }
    }
  }
}
<section class="content mt-0">
  <div class="container-fluid">
    <div class="row">
      <div class="col-md-12 ">
        <ol class="breadcrumb">
          <li class="breadcrumb-item"><a routerLink="/dashboard" routerLinkActive="active"><i class="nav-icon fas fa-tachometer-alt"></i>&nbsp;Dashboard</a></li>
          <li class="breadcrumb-item">In Check</li>
          <li class="breadcrumb-item active text-primary">In Check(ICD/Other)</li>
        </ol>
      </div>
    </div>
    <div class="row">
      <div class="card card-primary card-outline">
        <div class="card-body">
          <form [formGroup]="detailForm">
            <div class="row">
              <div class="col-md-6 col-lg-6 text-left">
                <h5 class="card-title">In Check(ICD/Other)</h5>
              </div>
              <div class="col-md-6 col-lg-6 text-end">
                <button *ngIf="!this.id" type="submit" class="btn btn-sm btn-primary mr-2 " [disabled]="!detailForm.valid"  (click)="onFormSubmit()"><i class="fa fa-save"></i>  &nbsp;Save</button>
                <a  class="btn btn-sm btn-secondary m-1"  href="/tms-operation/in-check">
                  <i class="fa fa-arrow-circle-left"></i> &nbsp; Back
                </a>
              </div>
            </div><br>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Yard</label>
                <ejs-textbox *ngIf="this.id" formControlName="inYardID" [readonly]="true" tabindex="1" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="inYardID" tabindex="1" [dataSource]='yardList' [fields]="{ text: 'yardID', value: 'yardID' }" (change)="onYardChange($event.value)" placeholder="Select One"  popupHeight='200px' ></ejs-dropdownlist>
                <div [style.visibility]='validateControl("inYardID")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Yard is required</label>
                </div>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Gate</label>
                <ejs-textbox *ngIf="this.id" formControlName="inGateID" [readonly]="true" tabindex="2" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id"  formControlName="inGateID" tabindex="2" [dataSource]='gateList' [fields]="{ text: 'gateID', value: 'gateID' }"  popupHeight='200px' placeholder="Select One" ></ejs-dropdownlist>
                <div [style.visibility]='validateControl("inGateID")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Gate is required</label>
                </div>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Category</label>
                <ejs-textbox *ngIf="this.id" formControlName="inPCCode" [readonly]="true" tabindex="3" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="inPCCode" tabindex="3" [dataSource]='pcCodeList' [fields]="{ text: 'pcCode', value: 'pcCode' }" allowFiltering="true" (change)="onCategoryChange($event.value)" placeholder="Select One"></ejs-dropdownlist>
                <div [style.visibility]='validateControl("inPCCode")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Category is required</label>
                </div>
            </div>
            </div>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Operation Area</label>
                <ejs-textbox *ngIf="this.id" formControlName="areaID" [readonly]="true" tabindex="4" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="areaID" tabindex="4" [dataSource]='areaList' [fields]="{ text: 'areaID', value: 'areaID' }" placeholder="Select One" ></ejs-dropdownlist>
                <div [style.visibility]='validateControl("areaID")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Operation Area is required</label>
                </div>
              </div>
              <div class="form-group col-md-4">
                <label class="font-weight-normal requiredfield">Check Date Time</label>
                <ejs-timepicker  formControlName="inCheckDateTime" [format]="format" [step]="interval" autocomplete="off" tabindex="5" [readonly]="this.id"></ejs-timepicker>
                <div [style.visibility]='validateControl("inCheckDateTime")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Check Date Time is required</label>
                </div>
               </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Truck Type</label>
                <ejs-textbox *ngIf="this.id" formControlName="truckType" [readonly]="true" tabindex="6" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id"  formControlName="truckType" tabindex="6" placeholder="Select One" [dataSource]='truckTypeList'  (change)="onTruckTypeChange($event.value)"></ejs-dropdownlist>
                <div [style.visibility]='validateControl("truckType")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Truck Type is required</label>
                </div>
              </div>
            </div>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Truck No</label>
                <ejs-textbox *ngIf="this.id" formControlName="truckVehicleRegNo" [readonly]="true" tabindex="7" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="truckVehicleRegNo" tabindex="7" placeholder="Enter some keys" [dataSource]='truckList' [fields]="{ text: 'vehicleRegNo', value: 'vehicleRegNo' }" (change)="onTruckChange($event.value)" allowFiltering="true" (filtering)='onTruckFiltering($event)'></ejs-dropdownlist>
                <div [style.visibility]='validateControl("truckVehicleRegNo")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Truck No is required</label>
                </div>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Trailer No</label>
                <ejs-textbox *ngIf="this.id" formControlName="trailerVehicleRegNo" [readonly]="true" tabindex="8" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="trailerVehicleRegNo" tabindex="8" [dataSource]='trailerList' [fields]="{ text: 'vehicleRegNo', value: 'vehicleRegNo' }" placeholder="Select One" allowFiltering="true" popupHeight='200px'></ejs-dropdownlist>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Driver Name</label>
                <ejs-textbox *ngIf="this.id" formControlName="driverLicenseNo" [readonly]="true" tabindex="9" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id"  formControlName="driverLicenseNo" tabindex="9" [dataSource]='driverList' placeholder="Enter some keys" [fields]="{ text: 'driverName', value: 'licenseNo' }" allowFiltering="true" (filtering)='onDriverFiltering($event)' popupHeight='200px'></ejs-dropdownlist>
                <div [style.visibility]='validateControl("driverLicenseNo")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Driver Name is required</label>
                </div>
              </div>
            </div>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Type</label>
                <ejs-textbox *ngIf="this.id" formControlName="inType" [readonly]="true" tabindex="10" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="inType" tabindex="10" [dataSource]='typeList'  placeholder="Select One" ></ejs-dropdownlist>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Cargo Type</label>
                <ejs-textbox *ngIf="this.id" formControlName="inCargoType" [readonly]="true" tabindex="11" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="inCargoType" tabindex="11" [dataSource]='cargoTypeList'  placeholder="Select One" ></ejs-dropdownlist>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">CargoInfo</label>
                <ejs-textbox formControlName="inCargoInfo" [multiline]="true" tabindex="12" [readonly]="this.id"></ejs-textbox>
              </div>
            </div>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Customer</label>
                <ejs-textbox formControlName="customer" [multiline]="true" tabindex="13" [readonly]="this.id"></ejs-textbox>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Remark</label>
                <ejs-textbox formControlName="remark" [multiline]="true" tabindex="14" [readonly]="this.id"></ejs-textbox>
              </div>
              <div class="form-group col-md-4" *ngIf="type!='RGL'">
                <label  class="font-weight-normal">WB Option</label>
                <ejs-textbox *ngIf="this.id" formControlName="wbOption" [readonly]="true" tabindex="15" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="wbOption" tabindex="15" [dataSource]='wbOptionList'  placeholder="Select One" ></ejs-dropdownlist>
              </div>
              <!-- <div class="form-group col-lg-2 col-md-2"><br>
                <ejs-checkbox formControlName="inboundWeight"  label="In Weight?"  autocomplete="off" floatLabelType="Always" tabindex="15" [disabled]="this.type=='RG'"></ejs-checkbox>
              </div>
              <div class="form-group col-lg-2 col-md-2"><br>
                <ejs-checkbox formControlName="outboundWeight"  label="Out Weight?"  autocomplete="off" floatLabelType="Always" tabindex="16" [disabled]="this.type=='RG'"></ejs-checkbox>
              </div> -->
            </div>
          </form><br>
          <h6>Documents</h6>
          <ejs-grid #Grid [dataSource]="docList" [allowSorting]="true" [allowPaging]="true"  [pageSettings]='pageSettings'  [editSettings]="editSettings" [toolbar]="toolbar" (toolbarClick)='toolbarClick($event)' [gridLines]="lines" (rowDataBound)='rowDataBound($event)'>
            <e-columns>
              <e-column type='checkbox' width='40' ></e-column>
              <e-column field='docCode' headerText="Doc No" width="100"></e-column>
              <e-column field='docName' headerText="Name" width="110"></e-column>
              <e-column field='checkStatus' headerText="Check Status" width="120"></e-column>
            </e-columns>
          </ejs-grid>
        </div>
      </div>
   </div>
 </div>
 <ejs-dialog #cardModel isModal="true"  [showCloseIcon]="true" height="55%" width="400px"  allowDragging="true" [visible]="false">
  <ng-template #header>
    <div class="font-weight-bold">Assign Card Form</div>
  </ng-template>
  <form [formGroup]="cardForm">
  <div class="row">
    <div class="form-group col-md-12">
      <label  class="requiredfield">Card</label>
      <ejs-dropdownlist formControlName="cardNo" tabindex="1" [dataSource]='cardList' [fields]="{ text: 'cardNo', value: 'cardNo' }" [allowFiltering]="true" placeholder="Select One" ></ejs-dropdownlist>
    </div>
   </div>
   <div class="row" *ngIf="this.isInWb">
    <div class="form-group col-md-6" *ngIf="this.isInWb">
      <label  class="requiredfield">In Weight Bridge</label>
      <ejs-dropdownlist formControlName="inWeightBridgeID" tabindex="2" [dataSource]='wbList' [fields]="{ text: 'weightBridgeID', value: 'weightBridgeID' }" [allowFiltering]="true" placeholder="Select One" ></ejs-dropdownlist>
    </div>
    <div class="form-group col-md-6">
      <label  class="requiredfield">In WB Option</label>
      <ejs-dropdownlist formControlName="inWBBillOption" tabindex="3" [dataSource]='billOptionList'  placeholder="Select One" ></ejs-dropdownlist>
    </div>
   </div>
   <div class="row"  *ngIf="this.isOutWb">
    <div class="form-group col-md-6">
      <label  class="requiredfield">Out Weight Bridge</label>
      <ejs-dropdownlist formControlName="outWeightBridgeID" tabindex="4" [dataSource]='wbList' [fields]="{ text: 'weightBridgeID', value: 'weightBridgeID' }" [allowFiltering]="true" placeholder="Select One" ></ejs-dropdownlist>
    </div>

    <div class="form-group col-md-6">
      <label  class="requiredfield">Out WB Option</label>
      <ejs-dropdownlist formControlName="outWBBillOption" tabindex="5" [dataSource]='billOptionList' placeholder="Select One" ></ejs-dropdownlist>
    </div>
   </div>
    <ng-template #footerTemplate>
      <button  class="e-edit-dialog e-control e-btn e-lib e-primary e-flat" type="submit" [disabled]="!this.cardForm.valid"  ejs-button (click)="onFormAssignCard()">Valid</button>
      <button  class="e-edit-dialog e-control e-btn e-lib e-flat" (click)="cardModel.hide()" ejs-button >Close</button>
    </ng-template>
  </form>
</ejs-dialog>
</section>
<ngx-spinner bdColor = "rgba(0, 0, 0, 0.8)" size = "medium" color = "#fff" type = "square-jelly-box" [fullScreen] = "true"><p style="color: white" > Loading... </p></ngx-spinner>
