import { Component, ViewChild } from '@angular/core';
import { TmsOperationModule } from '../../tms-operation.module';
import { EditSettingsModel, GridComponent, GridLine, PageSettingsModel, SaveEventArgs } from '@syncfusion/ej2-angular-grids';
import { NgxSpinnerService } from 'ngx-spinner';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { FilteringEventArgs } from '@syncfusion/ej2-angular-dropdowns';
import { EmitType } from '@syncfusion/ej2/base';
import { catchError, debounceTime, forkJoin, of, Subject, switchMap } from 'rxjs';
import { HttpErrorResponse } from '@angular/common/http';
import Swal from 'sweetalert2';
import { ClickEventArgs } from '@syncfusion/ej2-angular-navigations';
import { OutCheckService } from '../out-check/out-check.service';

@Component({
  selector: 'app-out-check-doc',
  standalone: true,
  imports: [TmsOperationModule],
  templateUrl: './out-check-doc.component.html',
  styleUrl: './out-check-doc.component.scss'
})
export class OutCheckDocComponent  {
  pageSettings: PageSettingsModel = { pageSize: 10 };
  editSettings: EditSettingsModel = { allowEditing: false, allowAdding: false, allowDeleting: true, mode: 'Dialog' };
  toolbar: any[] = [{ text: "Checked", tooltipText: "Checked", prefixIcon: "e-icons e-check", id: "check" },'Search'];
  lines: GridLine = 'Both';
  submitClicked:boolean=false;
  detailForm:any;
  id:any;
  interval: number=1;
  public format="dd/MM/yyyy h:mm:ss a";
  detailData:any;
  cargoTypeList:any[]=['Laden','MT'];
  typeList:string[]=['FCL','LCL'];
  yardList:any[]=[];
  gateList:any[]=[];
  pcCodeList:any[]=[];
  docList:any[]=[];
  cardList:any[]=[];
  endDate : Date = new Date();
  yard:string;
  today : Date = new Date();
  public data: Object[];
  public placeholder: string = 'Select One';
  public filterPlaceholder: string = 'Search';
  private searchCardTerms = new Subject<string>();
  @ViewChild('Grid') public grid: GridComponent;
  constructor(
    private service: OutCheckService,
    private spinner: NgxSpinnerService,
    private route: ActivatedRoute,
    private router: Router,

  ) {}

  ngOnInit(){
    this.id = this.route.snapshot.queryParams['id'];
    this.detailForm = new FormGroup({
    outRegNo: new FormControl(''),
    inRegNo: new FormControl(''),
    outCheckDateTime: new FormControl(this.today, Validators.required),
    outGateID: new FormControl('',Validators.required),
    outYardID: new FormControl('',Validators.required),
    outPCCode: new FormControl('',Validators.required),
    truckVehicleRegNo: new FormControl('',Validators.required),
    driverLicenseNo: new FormControl('',Validators.required),
    driverName: new FormControl(''),
    driverContactNo: new FormControl(''),
    areaID: new FormControl('',Validators.required),
    transporterID: new FormControl(''),
    transporterName: new FormControl(''),
    truckType:new FormControl('',Validators.required),
    trailerVehicleRegNo:new FormControl(''),
    outType:new FormControl(''),
    outCargoType:new FormControl(''),
    outCargoInfo:new FormControl(''),
    cardNo:new FormControl(''),
    remark:new FormControl(''),
    customer:new FormControl(''),
    status:new FormControl(''),
    outboundWeight:new FormControl(false),
    outWeightBridgeID: new FormControl(''),
    });

    this.getCategoryList();
    this.getLocationList();
    if(this.id){
      this.getOutBoundCheckById();
    }
    this.searchCardTerms.pipe(
      debounceTime(300),
      switchMap((term: string) => this.service.getCardICDList(term,this.yard))
    ).subscribe(data => {
      if(this.yard){
        this.cardList =data;
      }
    });
  }

  public onFiltering: EmitType<FilteringEventArgs> = (e: FilteringEventArgs) => {

  }


  onCardFiltering(e: any) {
    if (e.text && this.yard) {
      this.searchCardTerms.next(e.text);
    }
  }

  getLocationList() {
    this.spinner.show();
    this.service.getYardList('true')
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.yardList = result;
        this.spinner.hide();
    });
  }

  getGateList(yard:string){
    this.spinner.show();
    this.service.getGateList(yard)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.gateList  = result;
        this.spinner.hide();
    });
  }

  getDocumentSettingList(id:string){
    this.spinner.show();
    this.service.getDocumentSettingList(id)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.docList  = result;
        this.spinner.hide();
    });
  }

  getCategoryList(){
     this.service.getCategoryList()
     .pipe(catchError((err) => of(this.showError(err))))
       .subscribe((result) => {
         this.pcCodeList = result;
         this.pcCodeList.unshift({pcCode:'None'});
         this.spinner.hide();
     });
  }


  rowDataBound(args: any): void {
    if (args.row) {
      //args.isSelectable = args.data.status === 'Draft';
      if (args.data.checkStatus) {
        (args.row as Element).classList.add('bluegreen');
      }
     }
   }

   getOutBoundCheckById(){
    this.spinner.show();
    this.service.getOutBoundCheckById(this.id)
    .pipe(catchError((err) => of(this.showError(err))))
      .subscribe((result) => {
        this.docList=result.documentList;
        this.detailData = result;

        for (let key in this.detailData) {
          if ( this.detailData.hasOwnProperty(key) && this.detailData[key] != null &&  this.detailForm.controls[key]) {
            if (key != 'documentList') {
              this.detailForm.controls[key].setValue(this.detailData[key]);
            }
          }
         }
        });
        this.spinner.hide();
  }


  onFormSubmit(){
   this.spinner.show();
   const formData=this.detailForm.value;
   formData.outRegNo =0;
   formData.createdUser = localStorage.getItem('currentUser');
   const doc = this.docList.filter(x=>x.checkStatus!=true);
   if(doc.length>0 && this.docList.length>0){
    Swal.fire('Out Check Document', 'Please check document.', 'warning');
   }
   else{
    formData.documentList = this.docList;
    this.createOutBoundCheck(formData);
   }
   this.spinner.hide();
  }

  onBackSubmit(){
    this.router.navigate(["/tms-operation/out-check"]);
  }

  createOutBoundCheck(formData: any) {
    this.spinner.show();
    this.service
    .createOutBoundCheck(formData)
    .pipe(catchError((err) => of(this.showError(err))))
    .subscribe((result) => {
      if (result.status == true) {
        this.showSuccess(result.messageContent);
        this.router.navigate(["/tms-operation/out-check"]);
      } else {
        this.spinner.hide();
        Swal.fire('Out Check Document', result.messageContent, 'error');
      }
    });
  }

  onYardChange(code: string) {
    this.getGateList(code);
    this.yard =code;
   }

   onCategoryChange(code: string) {
    this.getDocumentSettingList(code);
   }



    onCardChange(id:string){
     const card = this.cardList.filter(x=>x.cardNo==id);
     if(card){
      this.detailForm.controls['inRegNo'].setValue(card[0].inRegNo?card[0].inRegNo:null);
      this.detailForm.controls['truckVehicleRegNo'].setValue(card[0].truckVehicleRegNo?card[0].truckVehicleRegNo:null);
      this.detailForm.controls['driverLicenseNo'].setValue(card[0].driverLicenseNo?card[0].driverLicenseNo:null);
      this.detailForm.controls['driverContactNo'].setValue(card[0].driverContactNo?card[0].driverContactNo:null);
      this.detailForm.controls['driverName'].setValue(card[0].driverName?card[0].driverName:null);
      this.detailForm.controls['transporterID'].setValue(card[0].transporterID?card[0].transporterID:null);
      this.detailForm.controls['transporterName'].setValue(card[0].transporterName?card[0].transporterName:null);
      this.detailForm.controls['areaID'].setValue(card[0].areaID?card[0].areaID:null);
      this.detailForm.controls['truckType'].setValue(card[0].truckType?card[0].truckType:null);
      this.detailForm.controls['trailerVehicleRegNo'].setValue(card[0].trailerVehicleRegNo?card[0].trailerVehicleRegNo:null);
      this.detailForm.controls['customer'].setValue(card[0].customer?card[0].customer:null);
      this.detailForm.controls['outboundWeight'].setValue(card[0].outboundWeight?card[0].outboundWeight:null);
      this.detailForm.controls['outWeightBridgeID'].setValue(card[0].outWeightBridgeID?card[0].outWeightBridgeID:null);
     }
    }



  showError(error:HttpErrorResponse){
    this.spinner.hide();
    Swal.fire('Out Check Document',error.statusText,'error');
  }
  showSuccess(msg: string) {
    this.spinner.hide();
    Swal.fire('Out Check Document', msg, 'success');
  }

  validateControl(controlName: string) {
    const control = this.detailForm.get(controlName);
    return (control.invalid && (control.dirty || control.touched)) || (control.invalid && this.submitClicked);
  }

  toolbarClick(args: ClickEventArgs): void {
    if (args.item.id === 'check') {
      let selectedRecords: any[] = this.grid.getSelectedRecords();
      if (selectedRecords.length == 0) {
        Swal.fire('Check Document', "Please select one row!", 'warning');
      }
      else {
        if (args.item.id === 'check')
        {
          for(var i=0;i<selectedRecords.length;i++){
            this.docList = this.docList.map((x) => {
              if (x.docCode === selectedRecords[i].docCode) {
                  return { ...x, docCode:  selectedRecords[i].docCode,docName: selectedRecords[i].docName,checkStatus: true};//update data
              } else {
                  return x; // Keep other objects unchanged
             }
             });
           }
        }
        return;
      }
    }
  }
}
<section class="content mt-0">
  <div class="container-fluid">
    <div class="row">
      <div class="col-md-12 ">
        <ol class="breadcrumb">
          <li class="breadcrumb-item"><a routerLink="/dashboard" routerLinkActive="active"><i class="nav-icon fas fa-tachometer-alt"></i>&nbsp;Dashboard</a></li>
          <li class="breadcrumb-item">Out Check</li>
          <li class="breadcrumb-item active text-primary">Out Check(ICD/Other)</li>
        </ol>
      </div>
    </div>
    <div class="row">
      <div class="card card-primary card-outline">
        <div class="card-body">
          <form [formGroup]="detailForm">
            <div class="row">
              <div class="col-md-6 col-lg-6 text-left">
                <h5 class="card-title">Out Check(ICD/Other)</h5>
              </div>
              <div class="col-md-6 col-lg-6 text-end">
                <button *ngIf="!this.id" type="submit" class="btn btn-sm btn-primary mr-2 " [disabled]="!detailForm.valid"  (click)="onFormSubmit()"><i class="fa fa-save"></i>  &nbsp;Save</button>
                <a  class="btn btn-sm btn-secondary m-1"  href="/tms-operation/out-check">
                  <i class="fa fa-arrow-circle-left"></i> &nbsp; Back
                </a>
              </div>
            </div><br>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Yard</label>
                <ejs-textbox *ngIf="this.id" formControlName="outYardID" [readonly]="true" tabindex="1" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="outYardID" tabindex="1" [dataSource]='yardList' [fields]="{ text: 'yardID', value: 'yardID' }" (change)="onYardChange($event.value)" placeholder="Select One" ></ejs-dropdownlist>
                <div [style.visibility]='validateControl("outYardID")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Yard is required</label>
                </div>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Gate</label>
                <ejs-textbox *ngIf="this.id" formControlName="outGateID" [readonly]="true" tabindex="2" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id"  formControlName="outGateID" tabindex="2" [dataSource]='gateList' [fields]="{ text: 'gateID', value: 'gateID' }"  placeholder="Select One" ></ejs-dropdownlist>
                <div [style.visibility]='validateControl("outGateID")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Gate is required</label>
                </div>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Card No</label>
                <ejs-textbox *ngIf="this.id" formControlName="cardNo" [readonly]="true" tabindex="3" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="cardNo" tabindex="3" [dataSource]='cardList' [fields]="{ text: 'cardNo', value: 'cardNo' }"  placeholder="Enter some keys" allowFiltering="true" (filtering)='onCardFiltering($event)'  (change)="onCardChange($event.value)"></ejs-dropdownlist>
                <div [style.visibility]='validateControl("cardNo")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*CardNo is required</label>
                </div>
              </div>
            </div>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal requiredfield">Category</label>
                <ejs-textbox *ngIf="this.id" formControlName="outPCCode" [readonly]="true" tabindex="4" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="outPCCode" tabindex="4" [dataSource]='pcCodeList' [fields]="{ text: 'pcCode', value: 'pcCode' }" allowFiltering="true" (change)="onCategoryChange($event.value)" placeholder="Select One"></ejs-dropdownlist>
                <div [style.visibility]='validateControl("outPCCode")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Category is required</label>
                </div>
              </div>
              <div class="form-group col-md-4">
                <label class="font-weight-normal requiredfield">Check Date Time</label>
                <ejs-timepicker  formControlName="outCheckDateTime" [format]="format" [step]="interval" autocomplete="off" tabindex="5" [readonly]="this.id"></ejs-timepicker>
                <div [style.visibility]='validateControl("outCheckDateTime")? "visible": "hidden"'>
                  <label class="e-error" style="display: block;">*Check Date Time is required</label>
                </div>
               </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Operation Area</label>
                <ejs-textbox formControlName="areaID" [readonly]="true" tabindex="6" ></ejs-textbox>
              </div>
            </div>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Truck Type</label>
                <ejs-textbox  formControlName="truckType" [readonly]="true" tabindex="7" ></ejs-textbox>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Truck No</label>
                <ejs-textbox formControlName="truckVehicleRegNo" [readonly]="true" tabindex="8" ></ejs-textbox>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Trailer No</label>
                <ejs-textbox formControlName="trailerVehicleRegNo" [readonly]="true" tabindex="9" ></ejs-textbox>
              </div>
            </div>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Driver Name</label>
                <ejs-textbox formControlName="driverLicenseNo" [readonly]="true" tabindex="10" ></ejs-textbox>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Type</label>
                <ejs-textbox *ngIf="this.id" formControlName="outType" [readonly]="true" tabindex="11" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="outType" tabindex="11" [dataSource]='typeList'  placeholder="Select One" ></ejs-dropdownlist>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Cargo Type</label>
                <ejs-textbox *ngIf="this.id" formControlName="outCargoType" [readonly]="true" tabindex="12" ></ejs-textbox>
                <ejs-dropdownlist *ngIf="!this.id" formControlName="outCargoType" tabindex="12" [dataSource]='cargoTypeList'  placeholder="Select One" ></ejs-dropdownlist>
              </div>
            </div>
            <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">CargoInfo</label>
                <ejs-textbox formControlName="outCargoInfo" [multiline]="true" tabindex="13" [readonly]="this.id"></ejs-textbox>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Customer</label>
                <ejs-textbox formControlName="customer" [multiline]="true" tabindex="14" [readonly]="this.id"></ejs-textbox>
              </div>
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Remark</label>
                <ejs-textbox formControlName="remark" [multiline]="true" tabindex="15" [readonly]="this.id"></ejs-textbox>
              </div>
            </div>
            <!-- <div class="row">
              <div class="form-group col-md-4">
                <label  class="font-weight-normal">Out WeightBridge ID</label>
                <ejs-textbox formControlName="outWeightBridgeID" tabindex="16" readonly="true"></ejs-textbox>
              </div>
              <div class="form-group col-lg-2 col-md-2"><br>
                <ejs-checkbox formControlName="outboundWeight"  label="Out Weight?"  autocomplete="off" floatLabelType="Always" tabindex="7"></ejs-checkbox>
              </div>
            </div> -->
          </form><br>
          <h6>Documents</h6>
          <ejs-grid #Grid [dataSource]="docList" [allowSorting]="true" [allowPaging]="true"  [pageSettings]='pageSettings'  [editSettings]="editSettings" [toolbar]="toolbar" (toolbarClick)='toolbarClick($event)' [gridLines]="lines" (rowDataBound)='rowDataBound($event)'>
            <e-columns>
              <e-column type='checkbox' width='40' ></e-column>
              <e-column field='docCode' headerText="Doc No" width="100"></e-column>
              <e-column field='docName' headerText="Name" width="110"></e-column>
              <e-column field='checkStatus' headerText="Check Status" width="120"></e-column>
            </e-columns>
          </ejs-grid>
        </div>
      </div>
   </div>
 </div>
</section>
